var dnnApp=angular.module("dnnApp",["ngRoute"]);dnnApp.config(function($routeProvider){$routeProvider.when("/",{templateUrl:"pages/home.html",controller:"mainController"}).when("/about",{templateUrl:"pages/about.html",controller:"aboutController"}).when("/event",{templateUrl:"pages/event.html",controller:"eventController"})});
dnnApp.controller("aboutController",function($scope){$scope.message="Contact us! JK. This is just a demo."});
dnnApp.controller("eventController",function($scope,eventService){$scope.event=eventService,$scope.message="What is going on!?"});
dnnApp.factory("eventService",["$rootScope",function($rootScope){var service={model:{timestamp:"",title:"",content:""},SaveState:function(){sessionStorage.eventService=angular.toJson(service.model)},RestoreState:function(){service.model=angular.fromJson(sessionStorage.eventService)}};return $rootScope.$on("savestate",service.SaveState),$rootScope.$on("restorestate",service.RestoreState),service}]);
dnnApp.controller("mainController",function($scope){$scope.message="Everyone come and see how good I look!"});